import React, { useContext, useState } from 'react';
import { AppContext } from '../context/AppContext';

const Currency = () => {
    const [symbol,setSymbol] = useState('£ pound');
    return (
    <div className='alert alert-secondary'>
    <span>Currency({symbol}</span> 
        <select className="custom-select" id="inputGroupSelect03" onChange={(event) => setSymbol(event.target.value)} style={{ marginLeft: '1px' , background:'lightgreen'}}>
            <option defaultValue>{symbol}</option>
            <option value="Euro" name="euro">€ Euro</option>
            <option value="Pound" name="pound">£ Pound</option>
            <option value="Ruppee" name="ruppee">₹ Rupee</option>
            <option value="Dollar" name="dollar">$ Dollar</option>
        </select>
    </div>
    );
};
export default Currency;
