import React, { useState } from 'react';

const Currency = () => {
    const [currency, setCurrency] = useState('£ Pound');

    const currencyHandler = (event) => {
        const selectedCurrency = event.target.value;
        setCurrency(selectedCurrency);

        dispatchCurrency(selectedCurrency); // Assuming dispatchCurrency is a function to dispatch the selected currency
    }

    const dispatchCurrency = (value) => {
        // Your dispatch logic here
        // For example:
        // dispatch({
        //     type: 'CHG_CURRENCY',
        //     payload: value,
        // })
    }

    return (
        <div className='alert alert-success'>
            <span>Currency(</span>
            <select
                className="custom-select"
                id="inputGroupSelect03"
                onChange={currencyHandler}
                value={currency} // Set the value of select based on the state
                style={{ marginLeft: '1px', background: 'lightgreen' }}
            >
                <option value="£ Pound">£ Pound</option>
                <option value="€ Euro">€ Euro</option>
                <option value="₹ Rupee">₹ Rupee</option>
                <option value="$ Dollar">$ Dollar</option>
            </select>
            <span>)</span>
        </div>
    );
};

export default Currency;
