    const currencyHandler = (event) => {
        const selectedCurrency = event.target.value;
        const symbol = getSymbolFromCurrency(selectedCurrency);
        const val="Currency("+selectedCurrency+")";
        dispatch({ type: 'CHG_CURRENCY', payload: symbol });
    }

