import React, {  useState } from 'react';
const Currency = () => {
    const [currency,setCurrency] = useState('£ Pound');
    return (
    <div className='alert alert-success'>
    <span>Currency(</span> 
        <select className="custom-select" id="inputGroupSelect03" onChange={(event) => setCurrency(event.target.value)} style={{ marginLeft: '1px' , background:'lightgreen'}}>
            <option defaultValue>{currency}</option>
            <option value="Euro" name="euro">£ Pound</option>
            <option value="Euro" name="euro">€ Euro</option>
            <option value="Ruppee" name="ruppee">₹ Rupee</option>
            <option value="Dollar" name="dollar">$ Dollar</option>
        </select>
        <span>)</span>
    </div>
    );
};
export default Currency;
